rue,"refInfo":{"name":"main","listCacheKey":"v0:1693554201.0","canEdit":true,"refType":"branch","currentOid":"a7598f548cc074c1ffd2299ba47f7091bf19b1eb"},"path":"scripts/l5_Pf_diffusion_2D.jl","currentUser":{"id":50524459,"login":"lcandiot","userEmail":"lorenzo.candioti@bluewin.ch"},"blob":{"rawLines":["using Plots,Plots.Measures,Printf\r","default(size=(600,500),framestyle=:box,label=false,grid=false,margin=10mm,lw=6,labelfontsize=11,tickfontsize=11,titlefontsize=11)\r","\r","function Pf_diffusion_2D()\r","    # physics\r","    lx,ly   = 20.0,20.0\r","    k_ηf    = 1.0\r","    # numerics\r","    nx,ny   = 127,127\r","    ϵtol    = 1e-8\r","    maxiter = 10max(nx,ny)\r","    ncheck  = ceil(Int,0.25max(nx,ny))\r","    cfl     = 1.0/sqrt(2.1)\r","    re      = 2π\r","    # derived numerics\r","    dx,dy   = lx/nx,ly/ny\r","    xc,yc   = LinRange(dx/2,lx-dx/2,nx),LinRange(dy/2,ly-dy/2,ny)\r","    θ_dτ    = max(lx,ly)/re/cfl/min(dx,dy)\r","    β_dτ    = (re*k_ηf)/(cfl*min(dx,dy)*max(lx,ly))\r","    # array initialisation\r","    Pf      = @. exp(-(xc-lx/2)^2 -(yc'-ly/2)^2)\r","    qDx,qDy = zeros(Float64, nx+1,ny),zeros(Float64, nx,ny+1)\r","    r_Pf    = zeros(nx,ny)\r","    # iteration loop\r","    iter = 1; err_Pf = 2ϵtol\r","    while err_Pf >= ϵtol && iter <= maxiter\r","        qDx[2:end-1,:] .-= (qDx[2:end-1,:] .+ k_ηf.*(diff(Pf,dims=1)./dx))./(1.0 + θ_dτ)\r","        qDy[:,2:end-1] .-= (qDy[:,2:end-1] .+ k_ηf.*(diff(Pf,dims=2)./dy))./(1.0 + θ_dτ)\r","        Pf             .-= (diff(qDx,dims=1)./dx .+ diff(qDy,dims=2)./dy)./β_dτ\r","        if iter%ncheck == 0\r","            r_Pf  .= diff(qDx,dims=1)./dx .+ diff(qDy,dims=2)./dy\r","            err_Pf = maximum(abs.(r_Pf))\r","            @printf(\"  iter/nx=%.1f, err_Pf=%1.3e\\n\",iter/nx,err_Pf)\r","            display(heatmap(xc,yc,Pf';xlims=(xc[1],xc[end]),ylims=(yc[1],yc[end]),aspect_ratio=1,c=:turbo,clim=(0,1)))\r","        end\r","        iter += 1\r","    end\r","    return\r","end\r","\r","Pf_diffusion_2D()\r"],"stylingDirectives":[[{"start":0,"end":5,"cssClass":"pl-k"},{"start":17,"end":18,"cssClass":"pl-k"}],[{"start":0,"end":7,"cssClass":"pl-c1"},{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":17,"cssClass":"pl-c1"},{"start":18,"end":21,"cssClass":"pl-c1"},{"start":33,"end":34,"cssClass":"pl-k"},{"start":34,"end":38,"cssClass":"pl-c1"},{"start":44,"end":45,"cssClass":"pl-k"},{"start":45,"end":50,"cssClass":"pl-c1"},{"start":55,"end":56,"cssClass":"pl-k"},{"start":56,"end":61,"cssClass":"pl-c1"},{"start":68,"end":69,"cssClass":"pl-k"},{"start":69,"end":71,"cssClass":"pl-c1"},{"start":76,"end":77,"cssClass":"pl-k"},{"start":77,"end":78,"cssClass":"pl-c1"},{"start":92,"end":93,"cssClass":"pl-k"},{"start":93,"end":95,"cssClass":"pl-c1"},{"start":108,"end":109,"cssClass":"pl-k"},{"start":109,"end":111,"cssClass":"pl-c1"},{"start":125,"end":126,"cssClass":"pl-k"},{"start":126,"end":128,"cssClass":"pl-c1"}],[],[{"start":0,"end":8,"cssClass":"pl-k"},{"start":9,"end":24,"cssClass":"pl-en"}],[{"start":4,"end":13,"cssClass":"pl-c"},{"start":4,"end":5,"cssClass":"pl-c"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":18,"cssClass":"pl-c1"},{"start":19,"end":23,"cssClass":"pl-c1"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":17,"cssClass":"pl-c1"}],[{"start":4,"end":14,"cssClass":"pl-c"},{"start":4,"end":5,"cssClass":"pl-c"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":17,"cssClass":"pl-c1"},{"start":18,"end":21,"cssClass":"pl-c1"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":18,"cssClass":"pl-c1"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":16,"cssClass":"pl-c1"},{"start":16,"end":19,"cssClass":"pl-c1"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":18,"cssClass":"pl-c1"},{"start":23,"end":27,"cssClass":"pl-c1"},{"start":27,"end":30,"cssClass":"pl-c1"}],[{"start":12,"end":13,"cssClass":"pl-k"},{"start":14,"end":17,"cssClass":"pl-c1"},{"start":17,"end":18,"cssClass":"pl-k"},{"star